/* global mapboxgl */
// eslint-disable-next-line no-unused-vars
var jekyllMapbox = (function() {
  'use strict'
  var mapReady = false
  var options = {}
  var data = []

  const MarkerIconDefault = 'data:image/png;base64,'

  // source: https://github.com/googlemaps/js-marker-clusterer/blob/gh-pages/images/m1.png
  const ClusterIconDefault = 'data:image/png;base64,'

  return {
    initializeMap: initializeMap,
    register: register
  }

  /**
   * Setup Google Maps options and call renderer.
   */
  function initializeMap(accessToken) {
    mapboxgl.accessToken = accessToken
    mapReady = true
    render()
  }

  /**
   * Register map data to be rendered once Google Maps API is loaded.
   *
   * @param string id
   * @param Array locations
   * @param Object settings
   */
  function register(id, geoJSON, options) {
    data.push({ id: id, geoJSON: geoJSON, options: options })
    render()
  }

  /**
   * Render maps data if Google Maps API is loaded.
   */
  function render() {
    if (!mapReady) return

    while (data.length > 0) {
      let item = data.pop()
      if(item.customZoom) item.zoom = item.customZoom // mapbox takes
      let mapOptions = Object.assign({
        container: item.id,
        style: 'mapbox://styles/mapbox/streets-v9',
      }, options, item.options)

      let map = new mapboxgl.Map(mapOptions)

      // get bounding box, see https://docs.mapbox.com/mapbox-gl-js/example/zoomto-linestring/
      // mapbox bounds: https://stackoverflow.com/a/35715102/1407622
      let bounds = item.geoJSON.features.reduce(function(bounds, feature) {
        if(!Array.isArray(feature.geometry.coordinates[0])) { // point feature
          return bounds.extend(feature.geometry.coordinates);
        } else {
          return feature.geometry.coordinates.reduce(function(bounds, coord) {
            return bounds.extend(coord);
          }, bounds);
        }
      }, new mapboxgl.LngLatBounds());

      map.fitBounds(bounds, {
        maxZoom: 12,
        padding: 30, // in px, to make markers on the top edge visible
        duration: 0, // disable zoom animation
      })

      // Add zoom and rotation controls to the map.
      map.addControl(new mapboxgl.NavigationControl());

      // Add geolocate control to the map.
      map.addControl(new mapboxgl.GeolocateControl({
        positionOptions: {
            enableHighAccuracy: true
        },
        trackUserLocation: true
      }));

      map.on('load', function() {

        map.addSource("features", {
          type: "geojson",
          data: item.geoJSON,
          cluster: mapOptions.useCluster,
        })

        // https://docs.mapbox.com/mapbox-gl-js/example/add-image/
        let markerIcon = mapOptions.markerIcon || MarkerIconDefault;
        map.loadImage(markerIcon, function(error, image) {
          if (error) throw error;
          map.addImage('marker-custom', image);

          map.addLayer({
            id: "unclustered-point",
            type: "symbol",
            source: "features",
            filter: ["!", ["has", "point_count"]],
            layout: {
              // "icon-image": "{marker-symbol}-15",
              "icon-image": "marker-custom",
              "icon-anchor": "bottom",
              "icon-size": 0.7,
              "icon-allow-overlap": true, // draw overlapping images (to be consistent with Google Map) when clustering is off
              // "text-field": "{title}",
              "text-font": ["Open Sans Semibold", "Arial Unicode MS Bold"],
              // "text-offset": [0, -1.6],
              // "text-anchor": "top"
            }
          });

          if(mapOptions.useCluster) {
            map.loadImage(ClusterIconDefault, function(error, image) {
              if (error) throw error;
              map.addImage('cluster-custom', image);

              map.addLayer({
                id: "clusters",
                type: "symbol",
                source: "features",
                filter: ["has", "point_count"],
                layout: {
                  "icon-image": "cluster-custom",
                  "icon-allow-overlap": true,
                  "text-allow-overlap": true,
                  "text-ignore-placement": true,
                }

              })

              map.addLayer({
                id: "cluster-count",
                type: "symbol",
                source: "features",
                filter: ["has", "point_count"],
                layout: {
                  "text-field": "{point_count_abbreviated}",
                  "text-font": ["DIN Offc Pro Medium", "Arial Unicode MS Bold"],
                  "text-size": 12,
                  "icon-allow-overlap": true,
                  "text-allow-overlap": true,
                  "text-ignore-placement": true,
                }
              });

              // inspect a cluster on click
              map.on('click', 'clusters', function (e) {
                let features = map.queryRenderedFeatures(e.point, { layers: ['clusters'] });
                let clusterId = features[0].properties.cluster_id;
                map.getSource('features').getClusterExpansionZoom(clusterId, function (err, zoom) {
                  if (err)
                    return;

                  map.easeTo({
                    center: features[0].geometry.coordinates,
                    zoom: zoom
                  });
                });
              });

              map.on('mouseenter', 'clusters', function () {
                map.getCanvas().style.cursor = 'pointer';
              });

              map.on('mouseleave', 'clusters', function () {
                map.getCanvas().style.cursor = '';
              });
            });
          }

          // https://docs.mapbox.com/mapbox-gl-js/example/popup-on-click/

          // When a click event occurs on a feature in the places layer, open a popup at the
          // location of the feature, with description HTML from its properties.
          map.on('click', 'unclustered-point', function (e) {
            let popup_html = e.features[0].properties["popup-html"];
            if(!popup_html) return;

            let coordinates = e.features[0].geometry.coordinates.slice();

            // Ensure that if the map is zoomed out such that multiple
            // copies of the feature are visible, the popup appears
            // over the copy being pointed to.
            while (Math.abs(e.lngLat.lng - coordinates[0]) > 180) {
                coordinates[0] += e.lngLat.lng > coordinates[0] ? 360 : -360;
            }

            // offset: https://bl.ocks.org/andrewharvey/60c0b1f12118bda230174ff630931278
            new mapboxgl.Popup({offset: [0, -40]})
                .setLngLat(coordinates)
                .setHTML(popup_html)
                .addTo(map);
          });

          // Change the cursor to a pointer when the mouse is over the places layer.
          map.on('mouseenter', 'unclustered-point', function () {
              map.getCanvas().style.cursor = 'pointer';
          });

          // Change it back to a pointer when it leaves.
          map.on('mouseleave', 'unclustered-point', function () {
              map.getCanvas().style.cursor = '';
          });
        });
      });
    }
  }
})()
/* Object.assign polyfill */
if (typeof Object.assign !== 'function') {
  Object.assign = function(target) {
    'use strict'
    if (target == null) {
      throw new TypeError('Cannot convert undefined or null to object')
    }

    target = Object(target)
    for (var index = 1; index < arguments.length; index++) {
      var source = arguments[index]
      if (source != null) {
        for (var key in source) {
          if (Object.prototype.hasOwnProperty.call(source, key)) {
            target[key] = source[key]
          }
        }
      }
    }
    return target
  }
}
